    //La operacion es global 
    
    Operacion OpAle= new Operacion();
    //Commbruba el resultado
    private void comprobar1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comprobar1ActionPerformed
 
        if(resnum1.getText()!="" && resden1.getText()!=""){
           Racional resultadoU = new Racional();
           resultadoU.setNumerador(Integer.parseInt(resnum1.getText()));
           resultadoU.setDenominador(Integer.parseInt(resden1.getText()));
           resultadoU.simpRacional();
           resultadoU.imprimir();
           if(OpAle.resultado.equalsRacional(resultadoU)){
               resnum1.setBackground(Color.green);
               resden1.setBackground(Color.green);
               System.out.println("El resultado es correcto ¡Felicidades!");
               System.out.println("Resultado Simplicado= ");
               OpAle.resultado.imprimir();
           }
           else{
                resnum1.setBackground(Color.red);
                resden1.setBackground(Color.red);
                System.out.println("El resultado correcto es= " );
                OpAle.resultado.imprimir();
           }
       }
    }//GEN-LAST:event_comprobar1ActionPerformed

