    //Esta funcion genera los numeros aleatorios
    private void comprobar4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comprobar4ActionPerformed
        
        num1.setText(Integer.toString(OpAle.num1.getNumerador()));
        den1.setText(Integer.toString(OpAle.num1.getDenominador()));
        num2.setText(Integer.toString(OpAle.num2.getNumerador()));
        den2.setText(Integer.toString(OpAle.num2.getDenominador()));
        op1.setText(Character.toString(OpAle.operador));
        //generamos el resultado
        OpAle.calculadora();
        
 
    }//GEN-LAST:event_comprobar4ActionPerformed

